function startTimer() {
  // if (test_type === "speed") {
  // Speed Test: Countdown timer
  if (!timerInterval) {
    let timeRemaining = totalTimeInSeconds;
    timerInterval = setInterval(() => {
      timeRemaining--;
      document.getElementById("countdown").textContent =
        formatTime(timeRemaining);
      if (timeRemaining <= 0) {
        stopTimer();
        alert("Time's up!");
      }
    }, 1000);
  }
}
